package Variablestudy3;

public class Floatdoublecharboolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a;
		a=99.9087789f;
		System.out.println("value of is " +a );
		
		double b;
		b=88.987656789d;
		System.out.println("value of is " +b);
		
        char grade;
		grade='A';
		System.out.println("value of is " +grade);
		
        boolean result;
		result=true;
		System.out.println("value of is " +result);
		

	}

}
